insert into Classes (title, field, speaker, details, current, max) values("말씀으로 하나님과 친밀해지기", "성경", "동성재 간사", "성경 말씀을 통해 단순히 읽는 행위가 아니라, 어떻게 하나님과 친밀해질 수 있는지, 성경을 통한 기도, 변화 등을 다루며 말씀 읽는 것이 더이상 의무가 아니라 기쁨의 시간이 되도록.", 0, 15);
